import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { getUser } from '../services/userAPI';

class Header extends Component {
  state = {
    user: '',
    loading: false,
  }

  componentDidMount = () => {
    this.setState({ loading: true });
    getUser().then((response) => this.setState(({
      user: response.name,
      loading: false,
    })));
  }

  render() {
    const { user, loading } = this.state;

    if (loading) {
      return <div>Carregando...</div>;
    }
    return (
      <header data-testid="header-component" className='flex justify-between'>
        <h1 className="text-6xl my-5 text-purple-800">
         {/* <img src={imageMusica} alt="Logo de nota musical" />  */}
         YourMusic
        </h1>
        <nav>
          {/* <Link to="/">Home </Link> */}
          <Link to="/profile" data-testid="link-to-profile">User </Link>
          {' '}
          <Link to="/search" data-testid="link-to-search">Pesquisar</Link>
          {' '}
          <Link to="/favorites" data-testid="link-to-favorites">Musicas Favoritas </Link>
        </nav>
        <p data-testid="header-user-name">
          {' '}
          {user}
        </p>
      </header>
    );
  }
}
export default Header;
