import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { getUser } from '../services/userAPI';

class Header extends Component {
  state = {
    user: '',
    loading: false,
  }

  componentDidMount = () => {
    this.setState({ loading: true });
    getUser().then((response) => this.setState(({
      user: response.name,
      loading: false,
    })));
  }

  render() {
    const { user, loading } = this.state;

    if (loading) {
      return <div>Carregando...</div>;
    }
    return (
      <><header data-testid="header-component" className='flex flex-row justify-between items-center py-2.5 px-5 bg-gray-100'>
        <h1 className="text-6xl my-4 text-purple-800">
          {/* <img src={imageMusica} alt="Logo de nota musical" />  */}
          YourMusic
        </h1>
        <nav>
          {/* <Link to="/">Home </Link> */}
          <Link to="/profile" data-testid="link-to-profile" className='mx-2.5'>User </Link>
          {' '}
          <Link to="/search" data-testid="link-to-search" className='mx-2.5'>Pesquisar</Link>
          {' '}
          <Link to="/favorites" data-testid="link-to-favorites" className='mx-2.5'>Musicas Favoritas </Link>
        </nav>
      </header>
      <main>
      <p data-testid="header-user-name" className='mb-2.5'>
       {' '}
       {`Boas-vindas, ${user}`}
     </p>
        </main></>
    );
  }
}
export default Header;
